import styled from 'styled-components';

const StyledInput = styled.input
`
    text-align: center;
    background: lightgrey;
    display: flex;
    flex: 3.5;
    width: 100%;
    max-width: 800px;
    padding: 5px 0px;
    margin: 15px auto;
    border-radius: 10px;
    box-shadow: 0px 0px 5px grey;
    border: none;
    outline: none;
    padding: 10px;
    transition: all 0.2s ease-in-out;

    &:hover {
        background: #BFC0C3;
    }
    &:focus {
        background: #AFB0B4;
    }
`;

const Input = ({width}) => {
    return (
        <StyledInput styl type="text" value="Search"/>
    )
}

export default Input;
