import styled from 'styled-components';
import React, { useState, useEffect } from 'react';
import FilterSearch from '../filtersearch/FilterSearch';

const database = {
    products: [
        {
            name: "oculus rift",
            img:"https://static.techspot.com/images/products/2019/audio-video/org/2019-05-02-product.png",
            fillDestination:100,
            currentFill: 98,
        },
        {
            name: "iphone 12 pro",
            img:"https://picsum.photos/200/300"
        },
        {
            name: "iphone 12 mini",
            img:"https://picsum.photos/201/300"
        },
        {
            name: "iphone xr",
            img:"https://picsum.photos/202/300"
        },
        {
            name: "iphell",
            img:"https://picsum.photos/202/300"
        },
        {
            name: "ipharon",
            img:"https://picsum.photos/202/300"
        },
        {
            name: "samsung tv",
            img:"https://picsum.photos/202/300"
        },
        {
            name: "kumkum",
            img:"https://picsum.photos/202/300"
        }
    ]
}
let StyledInput = styled.input`
    text-align: center;
    background: lightgrey;

    width: 100%;
    max-width: 800px;
    padding: 10px 0px;
    margin: 15px auto;

    border-radius: 10px;
    box-shadow: 0px 0px 5px grey;
    border: none;
    outline: none;
    transition: all 0.2s ease-in-out;
`;
const ReturnDiv = styled.div`
    position:relative;
    width: 100%;
    display: flex;
    flex-direction: column;
    flex: 1.5;
`
const Input = () => {
    const [filteredList, setFilteredList] = useState([]);
    const [searchValue, setSearchValue] = useState("");

    const handleInputChange = ((event) => {
        setSearchValue(event.target.value);
    })

    useEffect(() => {
        let filtered = [];
        if (searchValue.length >= 5) {
            filtered = database.products.filter(item =>
                item.name.toLowerCase().includes(searchValue)
            );
        }
        setFilteredList(filtered);
    }, [searchValue])


    useEffect(() => {
    }, [filteredList])

    return (
        <ReturnDiv>
            <StyledInput onChange={handleInputChange} type="text" placeholder="Search here" />
            <FilterSearch filteredList={filteredList} />
        </ReturnDiv>
    )
}

export default Input;
