    display: flex;
    flex-direction: row;
    justify-content: center;
    text-align: center;
    height: 250px;
    width: 75%;
    margin: 10px auto;
    border-radius: 20px;
    box-shadow: 0px 0px 5px black;
    padding: 15px;
    transition: all 0.2s ease-in-out;
    overflow-x:hidden;
    cursor: pointer;

    &:hover {
        background: #f5f5f5;
    }
`;

const Img = styled.img`
    height: 250px;
    align-self: start;
`

