import styled from 'styled-components';
import { useState } from 'react';


let StyledContainer = styled.div`
margin: 10px auto;
    width: 350px;
    display: flex;
    flex-direction: column;
`;
let StyledButton = styled.button`
border-radius: 5px;
    padding: 10px;
    width: 300px;
    margin: 10px auto;
    margin-top: 15px;
    background-color: rgb(170, 0, 255);
    border: none;
    outline: none;
    box-shadow: 0px 0px 5px black;
    cursor: pointer;
    transition: all 0.15s ease-in-out;
    color: white;
    text-transform: uppercase;
    font-weight: bold;
&:hover{
    background-color: rgb(119, 34, 162);
     }
`
let StyledInput = styled.input`
 margin: 5px auto;
    padding: 0px 10px;
    width: 300px;
    height: 40px;
    border-radius: 5px;
    outline: none;
    border: 1px solid rgb(194, 194, 194);
`


const SignUpRegularay = () => {
    const [FirstName,setFirstName] = useState('');
    const [LastName,setLastName] = useState('');
    const [Email,setEmail] = useState('');
    const [Password,setPassword] = useState('');

    const SignUp = () => {
        fetch('localhost:3000/register',{
            method: 'post',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({
                email:Email,
                name:FirstName + ' ' + LastName,
                password:Password,
                premission: 10

            })
        })
    }
    return (
        <StyledContainer>
            <StyledInput placeholder="First Name" type="text" />
            <StyledInput placeholder="Last name" type="text" />
            <StyledInput placeholder="Email Address" type="email" />
            <StyledInput placeholder="Password" type="password" />
            <StyledInput placeholder="Rewrite Password" type="password" />
            <StyledButton onClick={SignUp}>SIGN UP</StyledButton>
        </StyledContainer>
    )
}
export default SignUpRegularay;
