import styled from 'styled-components';
import SignUpWith from './SignUpWith';
import SignUpRegularay from './SignUpRegularay';

// const StyledButton = styled.button`
// width:70%;
// height:35px;
// margin:3px 0px;
// font-family:-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
// font-weight:bold;
// border-radius:10px;
// border:2px solid black;
// cursor: pointer;
// background-color:#ffffff;
// outline:none;
// &:hover{
//         background: rgba(255,255,255,.8);
//         color: black;
//      }
// `
const StyledTitle = styled.h1`
font-size:20px;
color:white;
margin:5px 0px;
`
const StyledContainer = styled.div`
    transition:all ease-in-out 0.8s;
    border:1px solid white;
    width: ${props=>props.widthValue};
    margin:0 auto;
    display:flex;
    justify-content:center;
    flex-direction:column;
    align-items:center;
    background-color:#0a101a;
    opacity: ${props => props.prop ? '1' : '0'};
    transform: ${props => props.prop ? 'translate(0%, 0%)' : 'translate(0%, -40%)'};
    filter:blur(2px);
`;
const SignUpPopup = ({showPopup}) => {
    return (
        <StyledContainer prop={showPopup} >
            <StyledTitle>Quick & Dirty</StyledTitle>
            <SignUpWith />
            <StyledTitle>Or</StyledTitle>
            <SignUpRegularay />
        </StyledContainer>


    )
}
export default SignUpPopup;
